import React, {Component} from 'react';
import {Container, ListGroup, ListGroupItem, Button} from 'reactstrap';
import {CSSTransition, TransitionGroup} from 'react-transition-group';
import uuid from 'uuid';
import {connect} from 'react-redux';
import {getClubs} from '../actions/clubActions';
import PropTypes from 'prop-types'; 



class ClubsList extends Component {

    componentDidMount() {
        this.pros.getClubs();
    }

    render() {
        const{clubs} = this.props.club;
        return(
            <Container>
            <Button 
            color="dark"
            style={{marginBottem: '2rem'}}
            onClick =  {() => {
                const name = prompt('Enter Club Name');
                if (name) {
                    this.setState(state => ({
                        clubs: [...state.clubs, {id: uuid(), name}]
                    }));
                }
            }}> Add Club
            </Button>
            <ListGroup> 
            <TransitionGroup className="clubs-list"> 
            {clubs.map(({id,name}) => (
                <CSSTransition key={id} timeout={500} classNames = "fade">
                <ListGroupItem>
                <Button
                className="remove-btn"
                color="danger"
                size="sm"
                onClick = {() => {
                    this.setState(state => ({
                        clubs: state.clubs.filter(club => club.id !== id)
                    }));
                }}
                >&times;</Button>
                {name}
                </ListGroupItem>
                </CSSTransition>
            ))}
            </TransitionGroup>
            </ListGroup>
            </Container>
        );
    }
}

ShoppingList.propTypes = {
    getClubsL: PropTypes.func.isRequired,
    club: PropTypes.object.isRequired
}


const mapStateToProps = (state) => ({
    club: state.club 
});

export default connect(mapStateToProps,{getClubs} ) (ClubsList);
