import axios from 'axios';
import{GET_CLUBS, ADD_CLUB, DELETE_CLUB} from './types';

export const getClubs = () => dispatch => {
dispatch(setClubsLoading());
axios
    .get('/api/clubs')
    .then(res=>
        dispatch({
            type: GET_CLUBS,
            payload: res.data
        })    
    )
};

export const deleteClub = (id) => {
    return {
        type: DELETE_CLUB,
        payload: id
    };
};

export const addClub = club => {
    return {
        type: ADD_CLUB,
        payload: club
    };
};

export const setClubsLoadin = () => {
    return {
        type: CLUBS_LOADING
    }
}
