import uuid from "uuid";
import{GET_CLUBS, ADD_CLUB, DELETE_CLUB} from '../actions/types';

const initialState = {
    clubs: [
            {id: uuid(), name: 'MCCF'},
            { id: uuid(), name: 'CCCF' },
            { id: uuid(), name: 'Cooking Club'}
    ]
} 

export default function(state = initialState, action) {
    switch(action.type) {
        case GET_CLUBS:
        return{
            ...state
        };
        case DELETE_CLUB:
        return {
            ...state,
            clubs: state.clubs.filter(club => club.id !== action.payload)
        };
        case ADD_CLUB:
            return{
            ...state,
            clubs: [action.payload, ...state.clubs]
            };
        default:
        return state;
    }
}
